import { useState } from "react";
import { Product, Category } from "../types";

interface Props {
  product: Product;
  category: Category | undefined;
  onEdit: (product: Product) => void;
  onDelete: (id: number) => void;
}

const ProductCard = ({ product, category, onEdit, onDelete }: Props) => {
  return (
    <div className="product-card">
      {product.imageUrl && (
        <img src={product.imageUrl} alt={product.productName} style={{ width: "100%", maxHeight: 120, objectFit: "cover", marginBottom: 8 }} />
      )}
      <h3>{product.productName}</h3>
      <p style={{ color: '#555', fontStyle: 'italic', marginBottom: 8 }}>{product.description}</p>
      <p>Fiyat: {product.price} TL</p>
      <p>Stok: {product.stockQuantity}</p>
      <p>Kategori: {category?.name || "Bilinmiyor"}</p>
      <div style={{ display: 'flex', gap: 8, marginTop: 8 }}>
        <button onClick={() => onEdit(product)}>Düzenle</button>
        <button onClick={() => onDelete(product.id)} style={{ background: '#e53e3e', color: '#fff' }}>Sil</button>
      </div>
    </div>
  );
};

export default ProductCard;
