import axios from "axios";
import { Product, Category } from "./types";

export const deleteProduct = async (id: number): Promise<void> => {
  await axios.delete(`${BASE_URL}/Product/${id}`);
};

type ProductUpdateDto = {
  id: number;
  productName: string;
  description: string;
  price: number;
  stockQuantity: number;
  imageUrl?: string;
  categoryId: number;
};

const BASE_URL = "http://localhost:5145/api";

export const updateProduct = async (product: Product): Promise<Product> => {
  // Sadece backend'in beklediği alanları gönder
  const dto: ProductUpdateDto = {
    id: product.id,
    productName: product.productName,
    description: product.description,
    price: product.price,
    stockQuantity: product.stockQuantity,
    imageUrl: product.imageUrl,
    categoryId: product.categoryId
  };
  const res = await axios.put(`${BASE_URL}/Product/${product.id}`, dto);
  return res.data;
};

export const getProducts = async (): Promise<Product[]> => {
  const res = await axios.get(`${BASE_URL}/Product`);
  return res.data;
};

export const getCategories = async (): Promise<Category[]> => {
  const res = await axios.get(`${BASE_URL}/Category`);
  return res.data;
};

export const addProduct = async (product: {
  productName: string;
  description: string;
  price: number;
  stockQuantity: number;
  imageUrl?: string;
  categoryId: number;
}): Promise<Product> => {
  const res = await axios.post(`${BASE_URL}/Product`, product);
  return res.data;
};

export const addCategory = async (name: string): Promise<Category> => {
  const res = await axios.post(`${BASE_URL}/Category`, { name });
  return res.data;
};

export const deleteCategory = async (id: number): Promise<void> => {
  await axios.delete(`${BASE_URL}/Category/${id}`);
};
