import { createContext, useState, ReactNode } from "react";
import { Product, Category } from "../types";

interface AppContextProps {
  products: Product[];
  categories: Category[];
  addProduct: (product: Product) => void;
  addCategory: (name: string) => void;
  deleteCategory: (id: number) => void;
}

export const AppContext = createContext<AppContextProps>({
  products: [],
  categories: [],
  addProduct: () => {},
  addCategory: () => {},
  deleteCategory: () => {},
});

const initialCategories: Category[] = [
  { id: 1, name: "Elektronik" },
  { id: 2, name: "Kitap" }
];

const initialProducts: Product[] = [
  { id: 1, name: "Laptop", description: "Güçlü bir laptop", price: 25000, categoryId: 1 },
  { id: 2, name: "Roman", description: "Sürükleyici bir roman", price: 150, categoryId: 2 }
];

export const AppProvider = ({ children }: { children: ReactNode }) => {
  const [products, setProducts] = useState<Product[]>(initialProducts);
  const [categories, setCategories] = useState<Category[]>(initialCategories);

  const addProduct = (product: Product) => setProducts(prev => [...prev, product]);
  const addCategory = (name: string) => setCategories(prev => [...prev, { id: Date.now(), name }]);
  const deleteCategory = (id: number) => setCategories(prev => prev.filter(cat => cat.id !== id));

  return (
    <AppContext.Provider value={{
      products,
      categories,
      addProduct,
      addCategory,
      deleteCategory
    }}>
      {children}
    </AppContext.Provider>
  );
};
